// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.7.5;

import "../interfaces/IPozzlePlanetAuthority.sol";

abstract contract PozzlePlanetAccessControlled {
    /* ========== EVENTS ========== */

    event AuthorityUpdated(IPozzlePlanetAuthority indexed authority);

    string UNAUTHORIZED = "UNAUTHORIZED"; // save gas

    /* ========== STATE VARIABLES ========== */

    IPozzlePlanetAuthority public authority;

    /* ========== Constructor ========== */

    constructor(IPozzlePlanetAuthority _authority) {
        authority = _authority;
        emit AuthorityUpdated(_authority);
    }

    /* ========== MODIFIERS ========== */

    modifier onlyGovernor() {
        require(msg.sender == authority.governor(), UNAUTHORIZED);
        _;
    }

    modifier onlyGuardian() {
        require(msg.sender == authority.guardian(), UNAUTHORIZED);
        _;
    }

    modifier onlyPolicy() {
        require(msg.sender == authority.policy(), UNAUTHORIZED);
        _;
    }

    modifier onlyVault() {
        require(msg.sender == authority.vault(), UNAUTHORIZED);
        _;
    }

    /* ========== GOV ONLY ========== */

    function setAuthority(IPozzlePlanetAuthority _newAuthority)
        external
        onlyGovernor
    {
        authority = _newAuthority;
        emit AuthorityUpdated(_newAuthority);
    }
}

